package com.iflytek.rule;import com.alibaba.druid.spring.boot.autoconfigure.DruidDataSourceAutoConfigure;import org.mybatis.spring.annotation.MapperScan;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.EnableAutoConfiguration;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.boot.autoconfigure.data.jpa.JpaRepositoriesAutoConfiguration;import org.springframework.boot.autoconfigure.data.mongo.MongoDataAutoConfiguration;import org.springframework.boot.autoconfigure.flyway.FlywayAutoConfiguration;import org.springframework.boot.autoconfigure.mongo.MongoAutoConfiguration;import org.springframework.boot.web.servlet.ServletComponentScan;//import org.springframework.boot.web.support.SpringBootServletInitializer;//import org.springframework.cloud.client.discovery.EnableDiscoveryClient;//import org.springframework.cloud.client.discovery.EnableDiscoveryClient;import org.springframework.context.annotation.ComponentScan;import org.springframework.context.annotation.EnableAspectJAutoProxy;import org.springframework.retry.annotation.EnableRetry;import org.springframework.scheduling.annotation.EnableAsync;import org.springframework.scheduling.annotation.EnableScheduling;import org.springframework.transaction.annotation.EnableTransactionManagement;/** * 程序启动类 * <br> * 标题: <br> * 描述: <br> * 公司: www.iflytek.com<br> * @autho dgyu * @time 2021年11月1日 上午10:42:58 */@SpringBootApplication(exclude = DruidDataSourceAutoConfigure.class)@EnableAsync@EnableScheduling@MapperScan(basePackages = {"com.iflytek.rule.mapper"})@ComponentScan(value ={"com.iflytek.rule","com.iflytek.icourt"})@ServletComponentScan@EnableAspectJAutoProxy@EnableTransactionManagement(proxyTargetClass = true)@EnableAutoConfiguration(exclude={        JpaRepositoriesAutoConfiguration.class //禁止springboot自动加载持久化bean        ,MongoAutoConfiguration.class, MongoDataAutoConfiguration.class,FlywayAutoConfiguration.class})//@EnableDiscoveryClient@EnableRetry(proxyTargetClass=true)public class CatalogRuleApplication  {	public static void main(String[] args) {		SpringApplication.run(CatalogRuleApplication.class, args);	}}